typedef int agr_handle_t;
void agr_close(agr_handle_t handle);

struct AGRBadPair {
    agr_handle_t left __attribute__((objc_resource(close=agr_close, invalid=-1)));
    agr_handle_t right __attribute__((objc_resource(close=agr_close, invalid=-1)));
};

int agr_neg01_use(struct AGRBadPair pair) {
    return pair.left + pair.right;
}
