id awt05_asyncCall(void) async;
id awt05_mainOnly(id seed) __attribute__((objc_executor(main)));
id awt05_syncValue(void);

actor class AWT05Counter : NSObject
- (int)current;
@end

id awt05_diag(AWT05Counter *counter, id seed) async __attribute__((objc_executor(global))) {
    id a = awt05_asyncCall();
    id b = awt05_mainOnly(seed);
    int c = [counter current];
    id d = await awt05_syncValue();
    return c > 0 ? d : (a ? a : b);
}
