id concdiag06_mainOnly(id seed) __attribute__((objc_executor(main)));

actor class CONCDIAG06Actor : NSObject
- (id _Nonnull)token;
@end

id concdiag06_positive(CONCDIAG06Actor *actor, id seed) async __attribute__((objc_executor(global))) {
    id hopped = await concdiag06_mainOnly(seed);
    id token = await [actor token];
    return token ? token : hopped;
}
