// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -fobjc3-concurrency=off -fsyntax-only %s
void spawnDetached(void (^operation)(void));
void useSharedState(id value);

async void diaggrp05_warn_capture(id sharedState) {
    spawnDetached(^{
        useSharedState(sharedState);
    });
}
