// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -fobjc3-performance=strict -fsyntax-only %s
@interface DIAGGRP09Box : NSObject
- (int)fastPath __attribute__((objc_direct));
@end

id diaggrp09_error_under_strict_performance(DIAGGRP09Box *box) {
    return [box performSelector:@selector(fastPath)];
}
