typedef id NSErrorRef;
int mig06_fetch(id key, id * _Nullable outValue, NSErrorRef * _Nullable outError);

id mig06_legacy_bridge(id key, NSErrorRef * _Nullable outError) {
    id value = nil;
    if (!mig06_fetch(key, &value, outError)) {
        return nil;
    }
    return value;
}

