@interface PERFDYN03Box : NSObject
- (int)fastPath __attribute__((objc_direct));
@end

void perfdyn03_warn_nsinvocation(PERFDYN03Box *box) {
    NSMethodSignature *sig = [box methodSignatureForSelector:@selector(fastPath)];
    NSInvocation *inv = [NSInvocation invocationWithMethodSignature:sig];
    [inv setSelector:@selector(fastPath)];
    [inv invokeWithTarget:box];
}
