typedef int res_handle_t;
void res_close(res_handle_t handle);

void res06_strict_system_diagnostics(int input) {
    res_handle_t fd __attribute__((objc_resource(close=res_close, invalid=-1))) = input;
    res_handle_t moved = take(fd);
    res_close(fd);
    discard(moved);
    discard(moved);
}
