int consumeNonnull(id _Nonnull value);
void stashBorrowed(const char * _Nullable *slot, borrowed const char *bytes);
void spawnDetached(void (^operation)(void));

async void runSystemMatrix(id _Nullable maybeValue,
                           borrowed const char *borrowedBytes,
                           const char * _Nullable *slot,
                           id sharedState) {
    consumeNonnull(maybeValue);
    stashBorrowed(slot, borrowedBytes);
    spawnDetached(^{
        (void)sharedState;
    });
}
