// RUN: objc3c -x objective-c++ -std=c++17 -fobjc-version=3 -fobjc3-strictness=strict-system -fobjc3-feature-aggregate-resources=on -fexceptions -O0 -fsyntax-only %s
// RUN: objc3c -x objective-c++ -std=c++17 -fobjc-version=3 -fobjc3-strictness=strict-system -fobjc3-feature-aggregate-resources=on -fexceptions -O2 -fsyntax-only %s
typedef int agr_handle_t;
static int agr_rt01_trace = 0;
static int agr_rt01_close_count_a = 0;
static int agr_rt01_close_count_b = 0;
static int agr_rt01_close_count_c = 0;

void agr_rt01_close_a(agr_handle_t handle) {
    if (handle != -1) {
        agr_rt01_trace = agr_rt01_trace * 10 + 1;
        agr_rt01_close_count_a = agr_rt01_close_count_a + 1;
    }
}

void agr_rt01_close_b(agr_handle_t handle) {
    if (handle != -1) {
        agr_rt01_trace = agr_rt01_trace * 10 + 2;
        agr_rt01_close_count_b = agr_rt01_close_count_b + 1;
    }
}

void agr_rt01_close_c(agr_handle_t handle) {
    if (handle != -1) {
        agr_rt01_trace = agr_rt01_trace * 10 + 3;
        agr_rt01_close_count_c = agr_rt01_close_count_c + 1;
    }
}

agr_handle_t agr_rt01_open_or_throw(int slot, int failAt) {
    if (slot == failAt) {
        throw slot;
    }
    return slot + 40;
}

struct AGRRT01Triple {
    agr_handle_t a __attribute__((objc_resource(close=agr_rt01_close_a, invalid=-1)));
    agr_handle_t b __attribute__((objc_resource(close=agr_rt01_close_b, invalid=-1)));
    agr_handle_t c __attribute__((objc_resource(close=agr_rt01_close_c, invalid=-1)));
};

int agr_rt01_partial_failure_trace(int failAt) {
    agr_rt01_trace = 0;
    agr_rt01_close_count_a = 0;
    agr_rt01_close_count_b = 0;
    agr_rt01_close_count_c = 0;

    try {
        struct AGRRT01Triple triple = { .a = -1, .b = -1, .c = -1 };
        triple.a = agr_rt01_open_or_throw(0, failAt);
        triple.b = agr_rt01_open_or_throw(1, failAt);
        triple.c = agr_rt01_open_or_throw(2, failAt);
        (void)triple;
    } catch (...) {
    }

    if (failAt == 0 && (agr_rt01_close_count_a != 0 || agr_rt01_close_count_b != 0 || agr_rt01_close_count_c != 0)) {
        return -1;
    }
    if (failAt == 1 && (agr_rt01_close_count_a != 1 || agr_rt01_close_count_b != 0 || agr_rt01_close_count_c != 0)) {
        return -1;
    }
    if (failAt == 2 && (agr_rt01_close_count_a != 1 || agr_rt01_close_count_b != 1 || agr_rt01_close_count_c != 0)) {
        return -1;
    }

    return agr_rt01_trace;
}
