// RUN: objc3c -fobjc-version=3 -fobjc-arc -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -O2 -fsyntax-only %s
@interface ARC67Node : NSObject
- (ARC67Node * _Nullable)next;
- (id _Nonnull)payload;
@end

id _Nullable arc67_preserve_arc_lowering(ARC67Node * _Nullable root,
                                         ARC67Node * _Nonnull fallback) {
    ARC67Node * _Nullable selected = [root? next] ?? fallback;
    return [selected? payload];
}
