// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O2 -fsyntax-only %s
void consumeOwned(__attribute__((ns_consumed)) id value);

id _Nonnull arc67_ownership_hazard(id _Nullable maybeValue,
                                   id _Nonnull fallback) {
    id candidate = maybeValue ?? fallback;
    consumeOwned(candidate);
    return candidate;
}
