// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
id<Error> can05_cancellation_error(void);

id can05_throwing_cancellation_point(void) async throws {
    throw can05_cancellation_error();
}

id can05_try_optionalize(void) async {
    return try? await can05_throwing_cancellation_point();
}

int can05_optional_is_empty_on_cancel(void) async {
    id value = try? await can05_throwing_cancellation_point();
    return value == nil ? 1 : 0;
}
