// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
int can06_task_is_cancelled(void);

id can06_hop_main(id value) async __attribute__((objc_executor(main)));
id can06_hop_worker(id value) async __attribute__((objc_executor(named("com.example.can06.worker"))));

@interface CAN06ActorLike : NSObject
- (id)consume:(id)value async __attribute__((objc_executor(named("com.example.can06.actor"))));
@end

id can06_hop_main(id value) async {
    return value;
}

id can06_hop_worker(id value) async {
    return value;
}

@implementation CAN06ActorLike
- (id)consume:(id)value async {
    return value;
}
@end

int can06_preserve_task_context(CAN06ActorLike *worker, id seed) async {
    int before = can06_task_is_cancelled();
    id stage1 = await can06_hop_main(seed);
    id stage2 = await can06_hop_worker(stage1);
    id stage3 = await [worker consume:stage2];
    int after = can06_task_is_cancelled();
    return (before == after && stage3 != nil) ? 1 : 0;
}
