// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
typedef id CAN07TaskHandle;
typedef enum CAN07WaitStatus {
    CAN07_WAIT_SUCCESS = 0,
    CAN07_WAIT_CANCELLED = 1,
    CAN07_WAIT_FAILURE = 2
} CAN07WaitStatus;

CAN07TaskHandle can07_spawn_child(id (^work)(void) async) __attribute__((objc_task_spawn));
void can07_request_current_task_cancellation(void);
CAN07WaitStatus can07_wait_nonthrowing(CAN07TaskHandle handle, id *resultOut) async;
int can07_task_is_cancelled(void);

int can07_nonthrowing_wait_reports_cancelled(id seed) async {
    CAN07TaskHandle handle = can07_spawn_child(^id(void) async {
        return can07_task_is_cancelled() != 0 ? nil : seed;
    });
    can07_request_current_task_cancellation();

    id result = nil;
    CAN07WaitStatus status = await can07_wait_nonthrowing(handle, &result);
    if (status == CAN07_WAIT_SUCCESS) {
        return -1;
    }
    if (status == CAN07_WAIT_CANCELLED && result != nil) {
        return -1;
    }
    return status == CAN07_WAIT_CANCELLED ? 1 : 0;
}
