// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
id coro01_read(id seed) async;
id coro01_merge(id lhs, id rhs) async;

id coro01_read(id seed) async {
    return seed;
}

id coro01_merge(id lhs, id rhs) async {
    return lhs ? lhs : rhs;
}

id coro01_pipeline(id seed) async {
    id first = await coro01_read(seed);
    id second = await coro01_merge(first, seed);
    return second;
}
