// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O0 --emit-module CORO02.objcmod --emit-interface CORO02.obji %s
// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O2 --emit-module CORO02.opt.objcmod --emit-interface CORO02.opt.obji %s
// RUN: objc3c --verify-interface CORO02.objcmod CORO02.obji %s
// RUN: objc3c --verify-interface CORO02.opt.objcmod CORO02.opt.obji %s
@interface CORO02Worker : NSObject
- (id _Nonnull)load:(id _Nonnull)seed async;
@end

@implementation CORO02Worker
- (id _Nonnull)load:(id _Nonnull)seed async {
    return seed;
}
@end

id coro02_call(CORO02Worker * _Nonnull worker, id _Nonnull seed) async {
    id stage = await [worker load:seed];
    return stage;
}
