// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O2 -fsyntax-only %s
id coro03_make(id seed) async;
id coro03_bump(id seed) async;

id coro03_make(id seed) async {
    return seed;
}

id coro03_bump(id seed) async {
    return seed;
}

int coro03_trace(id seed) async {
    id live = await coro03_make(seed);
    defer { (void)live; };
    id done = await coro03_bump(live);
    return done != nil ? 1 : 0;
}
