// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
typedef enum objc_async_completion_status {
    OBJC_ASYNC_COMPLETION_SUCCESS = 0,
    OBJC_ASYNC_COMPLETION_ERROR = 1,
    OBJC_ASYNC_COMPLETION_CANCELLED = 2
} objc_async_completion_status_t;

typedef void (*INTC08Completion)(
    void * _Nullable context,
    objc_async_completion_status_t status,
    id<Error> _Nullable error);

void intc08_flush(void) async throws;
void intc08_flush_async_c(void * _Nullable context,
                          INTC08Completion _Nonnull completion);

typedef struct INTC08Probe {
    int callbackCount;
    objc_async_completion_status_t lastStatus;
    id<Error> _Nullable lastError;
} INTC08Probe;

void intc08_probe_callback(void * _Nullable context,
                           objc_async_completion_status_t status,
                           id<Error> _Nullable error) {
    INTC08Probe *probe = (INTC08Probe *)context;
    probe->callbackCount = probe->callbackCount + 1;
    probe->lastStatus = status;
    probe->lastError = error;
}

void intc08_start_probe(INTC08Probe *probe) {
    probe->callbackCount = 0;
    intc08_flush_async_c(probe, intc08_probe_callback);
}
