// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 --emit-module INTC12.objcmod --emit-interface INTC12.obji %s
// RUN: objc3c --verify-interface INTC12.objcmod INTC12.obji %s
typedef enum objc_async_completion_status {
    OBJC_ASYNC_COMPLETION_SUCCESS = 0,
    OBJC_ASYNC_COMPLETION_ERROR = 1,
    OBJC_ASYNC_COMPLETION_CANCELLED = 2
} objc_async_completion_status_t;

typedef void (*INTC12Completion)(
    void * _Nullable context,
    objc_async_completion_status_t status,
    id _Nullable result,
    id<Error> _Nullable error);

@interface INTC12Service : NSObject
- (id _Nullable)compute:(int)token async throws;
@end

void intc12_service_compute_async_c(INTC12Service * _Nonnull service,
                                    int token,
                                    void * _Nullable context,
                                    INTC12Completion _Nonnull completion);

SEL intc12_runtime_selector(void) {
    return @selector(compute:);
}

typedef struct INTC12Probe {
    int callbackCount;
    objc_async_completion_status_t lastStatus;
    id<Error> _Nullable lastError;
} INTC12Probe;

void intc12_probe_callback(void * _Nullable context,
                           objc_async_completion_status_t status,
                           id _Nullable result,
                           id<Error> _Nullable error) {
    INTC12Probe *probe = (INTC12Probe *)context;
    probe->callbackCount = probe->callbackCount + 1;
    probe->lastStatus = status;
    probe->lastError = error;
    (void)result;
}

void intc12_start_probe(INTC12Service *service, int token, INTC12Probe *probe) {
    probe->callbackCount = 0;
    intc12_service_compute_async_c(service, token, probe, intc12_probe_callback);
}
