// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict-system -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict-system -O2 -fsyntax-only %s
@interface LIFE90Box : NSObject
- (id _Nonnull)payload;
@end

id _Nonnull life04_with_lifetime_arc(LIFE90Box * _Nonnull box,
                                     id _Nonnull fallback) {
    __attribute__((objc_precise_lifetime)) id _Nonnull pinned = fallback;
    withLifetime(box) {
        pinned = [box payload];
    }
    return pinned;
}
