// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict-system -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict-system -O2 -fsyntax-only %s
id _Nonnull life90_suspend_point(id _Nonnull value) async;
void life90_sink(id _Nonnull value);

id _Nonnull life05_keep_alive_async(id _Nonnull owner) async {
    keepAlive(owner);
    id _Nonnull resumed = await life90_suspend_point(owner);
    withLifetime(owner) {
        life90_sink(resumed);
    }
    return resumed;
}
