// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O0 --emit-module M01C002.objcmod --emit-interface M01C002.obji %s
// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O2 --emit-module M01C002.opt.objcmod --emit-interface M01C002.opt.obji %s
// RUN: objc3c --verify-interface M01C002.objcmod M01C002.obji --assert-mismatch async_completion.status_width:i32:i8
// RUN: objc3c --verify-interface M01C002.opt.objcmod M01C002.opt.obji --assert-mismatch async_completion.status_width:i32:i8
typedef enum M01C002Status {
    M01C002_STATUS_SUCCESS = 0,
    M01C002_STATUS_ERROR = 1,
    M01C002_STATUS_CANCELLED = 2
} M01C002Status;

typedef void (*M01C002Completion)(
    void * _Nullable context,
    M01C002Status status,
    id _Nullable result,
    id<Error> _Nullable error);

void m01c002_export_async(id _Nullable seed,
                          void * _Nullable context,
                          M01C002Completion _Nonnull completion);
