// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
typedef id M01C004TaskHandle;
typedef enum M01C004WaitStatus {
    M01C004_WAIT_SUCCESS = 0,
    M01C004_WAIT_CANCELLED = 1,
    M01C004_WAIT_FAILURE = 2
} M01C004WaitStatus;

M01C004TaskHandle m01c004_spawn(id (^work)(void) async) __attribute__((objc_task_spawn));
void m01c004_request_current_task_cancellation(void);
M01C004WaitStatus m01c004_wait_nonthrowing(M01C004TaskHandle handle, id *resultOut) async;

int m01c004_validate_cancelled_status(id seed) async {
    M01C004TaskHandle handle = m01c004_spawn(^id(void) async {
        return seed;
    });

    m01c004_request_current_task_cancellation();

    id result = nil;
    M01C004WaitStatus status = await m01c004_wait_nonthrowing(handle, &result);
    if (status == M01C004_WAIT_CANCELLED && result == nil) {
        return 1;
    }
    return -1;
}
