// RUN: objc3c -fobjc-version=3 -fobjc-arc -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -O2 -fsyntax-only %s
int m01c007_cleanup_counter = 0;

id m01c007_step(id seed) async {
    return seed;
}

int m01c007_trace(id seed) async {
    m01c007_cleanup_counter = 0;

    id live = await m01c007_step(seed);
    defer { m01c007_cleanup_counter = m01c007_cleanup_counter + 1; };

    id done = await m01c007_step(live);
    if (done == nil) {
        return -1;
    }

    return m01c007_cleanup_counter == 1 ? 1 : -1;
}
