// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O2 -fsyntax-only %s
typedef void (^M01C008Completion)(const char * _Nonnull bytes);
M01C008Completion m01c008_sink;

void m01c008_install_sink(void) {
    const char stackBytes[] = "m01c008";
    m01c008_sink = ^(const char * _Nonnull ignored) {
        (void)ignored;
        (void)stackBytes;
    };
}
