// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O0 --emit-module M03C007.objcmod --emit-interface M03C007.obji %s
// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O2 --emit-module M03C007.opt.objcmod --emit-interface M03C007.opt.obji %s
// RUN: objc3c --verify-interface M03C007.objcmod M03C007.obji --assert-mismatch neg_vector.callback_order:context,status,result,error --assert-mismatch neg_vector.status_error_pair:success+error --assert-mismatch neg_vector.cleanup_count:2:1
// RUN: objc3c --verify-interface M03C007.opt.objcmod M03C007.opt.obji --assert-mismatch neg_vector.callback_order:context,status,result,error --assert-mismatch neg_vector.status_error_pair:success+error --assert-mismatch neg_vector.cleanup_count:2:1
typedef enum M03C007CompletionStatus {
    M03C007_STATUS_SUCCESS = 0,
    M03C007_STATUS_ERROR = 1,
    M03C007_STATUS_CANCELLED = 2
} M03C007CompletionStatus;

typedef void (*M03C007Completion)(
    void * _Nullable context,
    M03C007CompletionStatus status,
    id _Nullable result,
    id<Error> _Nullable error);

void M03c007_dispatch(int token,
                      void * _Nullable context,
                      M03C007Completion _Nonnull completion);
