// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict -O2 -fsyntax-only %s
typedef enum M03C008BoundaryStatus {
    M03C008_BOUNDARY_OK = 0,
    M03C008_BOUNDARY_PRECONDITION_FAILURE = 1,
    M03C008_BOUNDARY_POSTCONDITION_FAILURE = 2
} M03C008BoundaryStatus;

typedef struct M03C008BoundaryProbe {
    int callbackCount;
    int entered;
    int exited;
} M03C008BoundaryProbe;

void M03c008_invoke_boundary(M03C008BoundaryProbe * _Nonnull probe,
                             void (^_Nonnull body)(M03C008BoundaryProbe * _Nonnull probe));

M03C008BoundaryStatus M03c008_validate_boundary(M03C008BoundaryProbe * _Nullable probe) {
    if (probe == nil) {
        return M03C008_BOUNDARY_PRECONDITION_FAILURE;
    }
    if (probe->entered != 1 || probe->exited != 1 || probe->callbackCount != 1) {
        return M03C008_BOUNDARY_POSTCONDITION_FAILURE;
    }
    return M03C008_BOUNDARY_OK;
}
