// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
typedef enum M07C011CompletionStatus {
    M07C011_STATUS_SUCCESS = 0,
    M07C011_STATUS_ERROR = 1,
    M07C011_STATUS_CANCELLED = 2
} M07C011CompletionStatus;

typedef void (*M07C011Completion)(
    void * _Nullable context,
    M07C011CompletionStatus status,
    id _Nullable result,
    id<Error> _Nullable error);

void M07C011_export_async(id _Nullable seed,
                          void * _Nullable context,
                          M07C011Completion _Nonnull completion);

typedef struct M07C011Probe {
    int callbackCount;
    M07C011CompletionStatus lastStatus;
    id _Nullable lastResult;
    id<Error> _Nullable lastError;
} M07C011Probe;

void M07C011_probe_callback(void * _Nullable context,
                            M07C011CompletionStatus status,
                            id _Nullable result,
                            id<Error> _Nullable error) {
    M07C011Probe *probe = (M07C011Probe *)context;
    probe->callbackCount = probe->callbackCount + 1;
    probe->lastStatus = status;
    probe->lastResult = result;
    probe->lastError = error;
}

int M07C011_validate_contract(id seed) {
    M07C011Probe probe;
    probe.callbackCount = 0;
    probe.lastStatus = M07C011_STATUS_CANCELLED;
    probe.lastResult = nil;
    probe.lastError = nil;

    M07C011_export_async(seed, &probe, M07C011_probe_callback);

    if (probe.callbackCount != 1) {
        return -1;
    }
    if (probe.lastStatus == M07C011_STATUS_SUCCESS && probe.lastError != nil) {
        return -1;
    }
    if (probe.lastStatus != M07C011_STATUS_SUCCESS && probe.lastResult != nil) {
        return -1;
    }
    return 1;
}
