// RUN: objc3c -fobjc-version=3 -O0 --emit-module M10C006A.objcmod --emit-interface M10C006A.obji %s
// RUN: objc3c -fobjc-version=3 -O0 --emit-module M10C006B.objcmod --emit-interface M10C006B.obji %s
// RUN: objc3c -fobjc-version=3 -O2 --emit-module M10C006A.opt.objcmod --emit-interface M10C006A.opt.obji %s
// RUN: objc3c -fobjc-version=3 -O2 --emit-module M10C006B.opt.objcmod --emit-interface M10C006B.opt.obji %s
// RUN: objc3c --verify-interface M10C006A.objcmod M10C006A.obji %s
// RUN: objc3c --verify-interface M10C006B.objcmod M10C006B.obji %s
// RUN: objc3c --verify-interface M10C006A.opt.objcmod M10C006A.opt.obji %s
// RUN: objc3c --verify-interface M10C006B.opt.objcmod M10C006B.opt.obji %s
typedef enum M10C006CompletionStatus {
    M10C006_STATUS_SUCCESS = 0,
    M10C006_STATUS_ERROR = 1,
    M10C006_STATUS_CANCELLED = 2
} M10C006CompletionStatus;

typedef void (*M10C006Completion)(
    void * _Nullable context,
    M10C006CompletionStatus status,
    id _Nullable result,
    id<Error> _Nullable error);

void M10c006_export_async_module_a(int token,
                                   void * _Nullable context,
                                   M10C006Completion _Nonnull completion);
void M10c006_export_async_module_b(int token,
                                   void * _Nullable context,
                                   M10C006Completion _Nonnull completion);
