// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
typedef enum M13C001CompletionStatus {
    M13C001_STATUS_SUCCESS = 0,
    M13C001_STATUS_ERROR = 1,
    M13C001_STATUS_CANCELLED = 2
} M13C001CompletionStatus;

typedef void (*M13C001Completion)(
    void * _Nullable context,
    M13C001CompletionStatus status,
    id _Nullable result,
    id<Error> _Nullable error);

void M13c001_export_async(id _Nullable seed,
                          void * _Nullable context,
                          M13C001Completion _Nonnull completion);

typedef struct M13C001Probe {
    int callbackCount;
    M13C001CompletionStatus lastStatus;
    id _Nullable lastResult;
    id<Error> _Nullable lastError;
} M13C001Probe;

void M13c001_probe_callback(void * _Nullable context,
                            M13C001CompletionStatus status,
                            id _Nullable result,
                            id<Error> _Nullable error) {
    M13C001Probe *probe = (M13C001Probe *)context;
    probe->callbackCount = probe->callbackCount + 1;
    probe->lastStatus = status;
    probe->lastResult = result;
    probe->lastError = error;
}

int M13c001_validate_contract(id seed) {
    M13C001Probe probe;
    probe.callbackCount = 0;
    probe.lastStatus = M13C001_STATUS_CANCELLED;
    probe.lastResult = nil;
    probe.lastError = nil;

    M13c001_export_async(seed, &probe, M13c001_probe_callback);

    if (probe.callbackCount != 1) {
        return -1;
    }
    if (probe.lastStatus == M13C001_STATUS_SUCCESS && probe.lastError != nil) {
        return -1;
    }
    if (probe.lastStatus != M13C001_STATUS_SUCCESS && probe.lastResult != nil) {
        return -1;
    }
    return 1;
}
