typedef enum M15C006CompletionStatus {
    M15C006_STATUS_SUCCESS = 0,
    M15C006_STATUS_ERROR = 1,
    M15C006_STATUS_CANCELLED = 2
} M15C006CompletionStatus;

typedef void (*M15C006Completion)(
    void * _Nullable context,
    M15C006CompletionStatus status,
    id _Nullable result,
    id<Error> _Nullable error);

void M15c006_export_async_module_a(int token,
                                   void * _Nullable context,
                                   M15C006Completion _Nonnull completion);
void M15c006_export_async_module_b(int token,
                                   void * _Nullable context,
                                   M15C006Completion _Nonnull completion);
