// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict -O2 -fsyntax-only %s
typedef enum M17C010InvariantStatus {
    M17C010_INVARIANT_OK = 0,
    M17C010_INVARIANT_CALL_SHAPE_DRIFT = 1,
    M17C010_INVARIANT_CLEANUP_DRIFT = 2,
    M17C010_INVARIANT_METADATA_DRIFT = 3
} M17C010InvariantStatus;

M17C010InvariantStatus M17C010_validate_invariants(int callShapeOk,
                                                    int cleanupOk,
                                                    int metadataOk) {
    if (!callShapeOk) {
        return M17C010_INVARIANT_CALL_SHAPE_DRIFT;
    }
    if (!cleanupOk) {
        return M17C010_INVARIANT_CLEANUP_DRIFT;
    }
    if (!metadataOk) {
        return M17C010_INVARIANT_METADATA_DRIFT;
    }
    return M17C010_INVARIANT_OK;
}
