// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
typedef int (*M21C013RuntimeHook)(
    void * _Nullable context,
    SEL _Nonnull selector,
    int stateTag,
    id _Nullable payload,
    id<Error> _Nullable * _Nullable errorOut);

M21C013RuntimeHook M21c013_resolve_hook(SEL _Nonnull selector);

int M21c013_dispatch(void * _Nullable context,
                     SEL _Nonnull selector,
                     int stateTag,
                     id _Nullable payload) {
    M21C013RuntimeHook hook = M21c013_resolve_hook(selector);
    if (hook == 0) {
        return -1;
    }

    id<Error> err = nil;
    int rc = hook(context, selector, stateTag, payload, &err);
    if (rc == 0 && err == nil) {
        return 1;
    }
    return -1;
}
