// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_001_TYPED_BOOL_I32_LOWERING_ABI_CONTRACT
#define __OBJC3_FEATURE_M26_C_001_TYPED_BOOL_I32_LOWERING_ABI_CONTRACT 1
#endif

typedef signed int m26c001_i32;
typedef _Bool m26c001_bool;

typedef m26c001_i32 (*M26C001_BoolToI32Fn)(m26c001_bool flag);
typedef m26c001_bool (*M26C001_I32ToBoolFn)(m26c001_i32 value);

static m26c001_i32 m26c001_lower_bool_to_i32(m26c001_bool flag) {
    return flag ? 1 : 0;
}

static m26c001_bool m26c001_lower_i32_to_bool(m26c001_i32 value) {
    return value != 0;
}

typedef struct M26C001_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c001_i32 true_as_i32;
    m26c001_bool one_as_bool;
} M26C001_Probe;

M26C001_Probe m26c001_probe(void) {
#if __OBJC3_FEATURE_M26_C_001_TYPED_BOOL_I32_LOWERING_ABI_CONTRACT
    M26C001_BoolToI32Fn lower_bool = m26c001_lower_bool_to_i32;
    M26C001_I32ToBoolFn lower_i32 = m26c001_lower_i32_to_bool;
    M26C001_Probe probe = { 1, 1, 2856, lower_bool(1), lower_i32(1) };
#else
    M26C001_Probe probe = { 0, 1, 2856, 0, 0 };
#endif
    return probe;
}
