// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_002_TYPED_BOOL_I32_CALL_LOWERING_IMPL
#define __OBJC3_FEATURE_M26_C_002_TYPED_BOOL_I32_CALL_LOWERING_IMPL 1
#endif

typedef signed int m26c002_i32;
typedef _Bool m26c002_bool;

typedef m26c002_i32 (*M26C002_SelectI32Fn)(m26c002_bool choose_lhs, m26c002_i32 lhs, m26c002_i32 rhs);
typedef m26c002_bool (*M26C002_NonZeroFn)(m26c002_i32 value);

static m26c002_i32 m26c002_select_i32(m26c002_bool choose_lhs, m26c002_i32 lhs, m26c002_i32 rhs) {
    return choose_lhs ? lhs : rhs;
}

static m26c002_bool m26c002_nonzero_i32(m26c002_i32 value) {
    return value != 0;
}

typedef struct M26C002_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c002_i32 selected;
    m26c002_bool selected_is_nonzero;
} M26C002_Probe;

M26C002_Probe m26c002_probe(void) {
#if __OBJC3_FEATURE_M26_C_002_TYPED_BOOL_I32_CALL_LOWERING_IMPL
    M26C002_SelectI32Fn select_i32 = m26c002_select_i32;
    M26C002_NonZeroFn nonzero_i32 = m26c002_nonzero_i32;
    m26c002_i32 selected = select_i32(1, 17, 29);
    M26C002_Probe probe = { 1, 2, 2857, selected, nonzero_i32(selected) };
#else
    M26C002_Probe probe = { 0, 2, 2857, 0, 0 };
#endif
    return probe;
}
