// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_003_BOOL_SLOT_CANONICALIZATION_HARDENING
#define __OBJC3_FEATURE_M26_C_003_BOOL_SLOT_CANONICALIZATION_HARDENING 1
#endif

typedef signed int m26c003_i32;
typedef _Bool m26c003_bool;

typedef m26c003_bool (*M26C003_CanonicalizeBoolFn)(m26c003_i32 raw_bool_slot);
typedef m26c003_i32 (*M26C003_GuardedI32Fn)(m26c003_i32 raw_bool_slot, m26c003_i32 payload);

static m26c003_bool m26c003_canonicalize_bool(m26c003_i32 raw_bool_slot) {
    return raw_bool_slot != 0;
}

static m26c003_i32 m26c003_guarded_i32(m26c003_i32 raw_bool_slot, m26c003_i32 payload) {
    m26c003_bool enabled = m26c003_canonicalize_bool(raw_bool_slot);
    return enabled ? payload : -payload;
}

typedef struct M26C003_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c003_bool canonicalized_nonzero;
    m26c003_bool canonicalized_zero;
    m26c003_i32 guarded_nonzero;
    m26c003_i32 guarded_zero;
} M26C003_Probe;

M26C003_Probe m26c003_probe(void) {
#if __OBJC3_FEATURE_M26_C_003_BOOL_SLOT_CANONICALIZATION_HARDENING
    M26C003_CanonicalizeBoolFn canonicalize = m26c003_canonicalize_bool;
    M26C003_GuardedI32Fn guarded = m26c003_guarded_i32;
    M26C003_Probe probe = { 1, 3, 2858, canonicalize(-7), canonicalize(0), guarded(9, 21), guarded(0, 21) };
#else
    M26C003_Probe probe = { 0, 3, 2858, 0, 0, 0, 0 };
#endif
    return probe;
}
