// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_004_TYPED_BOOL_I32_CONFORMANCE_MATRIX
#define __OBJC3_FEATURE_M26_C_004_TYPED_BOOL_I32_CONFORMANCE_MATRIX 1
#endif

typedef signed int m26c004_i32;
typedef _Bool m26c004_bool;

typedef m26c004_i32 (*M26C004_BoolToI32Fn)(m26c004_bool value);
typedef m26c004_bool (*M26C004_I32ToBoolFn)(m26c004_i32 value);

static m26c004_i32 m26c004_bool_to_i32(m26c004_bool value) {
    return value ? 1 : 0;
}

static m26c004_bool m26c004_i32_to_bool(m26c004_i32 value) {
    return value != 0;
}

static m26c004_i32 m26c004_case_score(m26c004_bool flag_arg, m26c004_i32 value_arg) {
    m26c004_i32 flag_lane = m26c004_bool_to_i32(flag_arg);
    m26c004_i32 value_lane = m26c004_bool_to_i32(m26c004_i32_to_bool(value_arg));
    return (flag_lane << 1) | value_lane;
}

typedef struct M26C004_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c004_i32 matrix_checksum;
    m26c004_bool roundtrip_nonzero;
} M26C004_Probe;

M26C004_Probe m26c004_probe(void) {
#if __OBJC3_FEATURE_M26_C_004_TYPED_BOOL_I32_CONFORMANCE_MATRIX
    M26C004_BoolToI32Fn bool_to_i32 = m26c004_bool_to_i32;
    M26C004_I32ToBoolFn i32_to_bool = m26c004_i32_to_bool;
    m26c004_i32 checksum = 0;
    checksum += m26c004_case_score(0, 0);
    checksum += m26c004_case_score(0, 7);
    checksum += m26c004_case_score(1, 0);
    checksum += m26c004_case_score(1, -3);
    M26C004_Probe probe = { 1, 4, 2859, checksum + bool_to_i32(i32_to_bool(42)), i32_to_bool(-3) };
#else
    M26C004_Probe probe = { 0, 4, 2859, 0, 0 };
#endif
    return probe;
}
