// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_005_TYPED_BOOL_I32_NORMALIZATION_DETERMINISM
#define __OBJC3_FEATURE_M26_C_005_TYPED_BOOL_I32_NORMALIZATION_DETERMINISM 1
#endif

typedef signed int m26c005_i32;
typedef _Bool m26c005_bool;

static m26c005_i32 m26c005_bool_to_i32(m26c005_bool value) {
    return value ? 1 : 0;
}

static m26c005_bool m26c005_i32_to_bool(m26c005_i32 value) {
    return value != 0;
}

static m26c005_i32 m26c005_normalize_case(m26c005_i32 lhs, m26c005_i32 rhs) {
    m26c005_bool lhs_flag = m26c005_i32_to_bool(lhs);
    m26c005_bool rhs_flag = m26c005_i32_to_bool(rhs);
    return (m26c005_bool_to_i32(lhs_flag) << 1) | m26c005_bool_to_i32(rhs_flag);
}

typedef struct M26C005_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c005_i32 normalization_digest;
} M26C005_Probe;

M26C005_Probe m26c005_probe(void) {
#if __OBJC3_FEATURE_M26_C_005_TYPED_BOOL_I32_NORMALIZATION_DETERMINISM
    m26c005_i32 digest = 0;
    digest = (digest << 3) ^ m26c005_normalize_case(0, 0);
    digest = (digest << 3) ^ m26c005_normalize_case(0, 7);
    digest = (digest << 3) ^ m26c005_normalize_case(-9, 0);
    digest = (digest << 3) ^ m26c005_normalize_case(-9, 5);
    M26C005_Probe probe = { 1, 5, 2860, digest };
#else
    M26C005_Probe probe = { 0, 5, 2860, 0 };
#endif
    return probe;
}
