// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_006_TYPED_BOOL_I32_REGRESSION_CLOSURE
#define __OBJC3_FEATURE_M26_C_006_TYPED_BOOL_I32_REGRESSION_CLOSURE 1
#endif

typedef signed int m26c006_i32;
typedef _Bool m26c006_bool;

static m26c006_bool m26c006_threshold_gate(m26c006_i32 value) {
    return value > 3;
}

static m26c006_i32 m26c006_merge_gate(m26c006_i32 lhs, m26c006_i32 rhs, m26c006_bool gate) {
    return gate ? lhs : rhs;
}

static m26c006_i32 m26c006_run_vector(m26c006_i32 a, m26c006_i32 b) {
    m26c006_bool gate = m26c006_threshold_gate(a + b);
    m26c006_i32 merged = m26c006_merge_gate(a, b, gate);
    return merged + (gate ? 1 : 0);
}

typedef struct M26C006_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c006_i32 regression_score;
    m26c006_bool gate_state;
} M26C006_Probe;

M26C006_Probe m26c006_probe(void) {
#if __OBJC3_FEATURE_M26_C_006_TYPED_BOOL_I32_REGRESSION_CLOSURE
    m26c006_i32 score = 0;
    score += m26c006_run_vector(2, 1);
    score += m26c006_run_vector(3, 2);
    score += m26c006_run_vector(4, 1);
    M26C006_Probe probe = { 1, 6, 2861, score, m26c006_threshold_gate(score) };
#else
    M26C006_Probe probe = { 0, 6, 2861, 0, 0 };
#endif
    return probe;
}
