// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_007_TYPED_BOOL_I32_CALL_RETURN_BOUNDARIES
#define __OBJC3_FEATURE_M26_C_007_TYPED_BOOL_I32_CALL_RETURN_BOUNDARIES 1
#endif

typedef signed int m26c007_i32;
typedef _Bool m26c007_bool;

typedef m26c007_bool (*M26C007_I32ToBoolFn)(m26c007_i32 value);
typedef m26c007_i32 (*M26C007_BoolToI32Fn)(m26c007_bool value);

static m26c007_bool m26c007_i32_to_bool(m26c007_i32 value) {
    return value != 0;
}

static m26c007_i32 m26c007_bool_to_i32(m26c007_bool value) {
    return value ? 1 : 0;
}

static m26c007_i32 m26c007_boundary_digest(M26C007_I32ToBoolFn i32_to_bool, M26C007_BoolToI32Fn bool_to_i32) {
    m26c007_i32 digest = 0;
    digest = (digest << 2) | bool_to_i32(i32_to_bool(0));
    digest = (digest << 2) | bool_to_i32(i32_to_bool(1));
    digest = (digest << 2) | bool_to_i32(i32_to_bool(-2147483647 - 1));
    digest = (digest << 2) | bool_to_i32(i32_to_bool(2147483647));
    return digest;
}

typedef struct M26C007_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c007_i32 boundary_digest;
    m26c007_bool min_roundtrip;
    m26c007_bool max_roundtrip;
} M26C007_Probe;

M26C007_Probe m26c007_probe(void) {
#if __OBJC3_FEATURE_M26_C_007_TYPED_BOOL_I32_CALL_RETURN_BOUNDARIES
    M26C007_I32ToBoolFn i32_to_bool = m26c007_i32_to_bool;
    M26C007_BoolToI32Fn bool_to_i32 = m26c007_bool_to_i32;
    M26C007_Probe probe = {
        1,
        7,
        2862,
        m26c007_boundary_digest(i32_to_bool, bool_to_i32),
        i32_to_bool(-2147483647 - 1),
        i32_to_bool(2147483647)
    };
#else
    M26C007_Probe probe = { 0, 7, 2862, 0, 0, 0 };
#endif
    return probe;
}
