// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_008_TYPED_BOOL_I32_INTEGRATION_STABILIZATION
#define __OBJC3_FEATURE_M26_C_008_TYPED_BOOL_I32_INTEGRATION_STABILIZATION 1
#endif

typedef signed int m26c008_i32;
typedef _Bool m26c008_bool;

typedef m26c008_i32 (*M26C008_ProducerFn)(m26c008_bool gate, m26c008_i32 payload);
typedef m26c008_bool (*M26C008_ConsumerFn)(m26c008_i32 lane_value);

static m26c008_i32 m26c008_producer(m26c008_bool gate, m26c008_i32 payload) {
    return gate ? payload : -payload;
}

static m26c008_bool m26c008_consumer(m26c008_i32 lane_value) {
    return lane_value > 0;
}

static m26c008_i32 m26c008_stabilize(M26C008_ProducerFn producer, M26C008_ConsumerFn consumer, m26c008_bool gate, m26c008_i32 payload) {
    m26c008_i32 lane_value = producer(gate, payload);
    return lane_value + (consumer(lane_value) ? 1 : 0);
}

typedef struct M26C008_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c008_i32 integration_score;
    m26c008_bool stabilized_gate;
} M26C008_Probe;

M26C008_Probe m26c008_probe(void) {
#if __OBJC3_FEATURE_M26_C_008_TYPED_BOOL_I32_INTEGRATION_STABILIZATION
    M26C008_ProducerFn producer = m26c008_producer;
    M26C008_ConsumerFn consumer = m26c008_consumer;
    m26c008_i32 score = 0;
    score += m26c008_stabilize(producer, consumer, 1, 9);
    score += m26c008_stabilize(producer, consumer, 0, 9);
    score += m26c008_stabilize(producer, consumer, 1, 3);
    M26C008_Probe probe = { 1, 8, 2863, score, consumer(score) };
#else
    M26C008_Probe probe = { 0, 8, 2863, 0, 0 };
#endif
    return probe;
}
