// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_009_DOCS_EVIDENCE_COMPILE_CONTRACTS
#define __OBJC3_FEATURE_M26_C_009_DOCS_EVIDENCE_COMPILE_CONTRACTS 1
#endif

typedef signed int m26c009_i32;
typedef _Bool m26c009_bool;

typedef struct M26C009_ContractEvidence {
    m26c009_i32 checklist_e_3_11;
    m26c009_i32 interop_part_11_2_4;
    m26c009_i32 lowering_contract_c_5;
    m26c009_i32 metadata_d_3_2;
} M26C009_ContractEvidence;

typedef m26c009_bool (*M26C009_ContractsSatisfiedFn)(M26C009_ContractEvidence evidence);
typedef m26c009_i32 (*M26C009_ContractDigestFn)(M26C009_ContractEvidence evidence);

static m26c009_bool m26c009_contracts_satisfied(M26C009_ContractEvidence evidence) {
    return evidence.checklist_e_3_11 && evidence.interop_part_11_2_4 && evidence.lowering_contract_c_5 && evidence.metadata_d_3_2;
}

static m26c009_i32 m26c009_contract_digest(M26C009_ContractEvidence evidence) {
    m26c009_i32 digest = 0;
    digest = (digest << 1) | (evidence.checklist_e_3_11 != 0);
    digest = (digest << 1) | (evidence.interop_part_11_2_4 != 0);
    digest = (digest << 1) | (evidence.lowering_contract_c_5 != 0);
    digest = (digest << 1) | (evidence.metadata_d_3_2 != 0);
    return digest;
}

typedef struct M26C009_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c009_i32 contract_digest;
    m26c009_bool contracts_satisfied;
} M26C009_Probe;

M26C009_Probe m26c009_probe(void) {
#if __OBJC3_FEATURE_M26_C_009_DOCS_EVIDENCE_COMPILE_CONTRACTS
    M26C009_ContractsSatisfiedFn contracts_satisfied = m26c009_contracts_satisfied;
    M26C009_ContractDigestFn contract_digest = m26c009_contract_digest;
    M26C009_ContractEvidence evidence = { 1, 1, 1, 1 };
    M26C009_Probe probe = { 1, 9, 2864, contract_digest(evidence), contracts_satisfied(evidence) };
#else
    M26C009_Probe probe = { 0, 9, 2864, 0, 0 };
#endif
    return probe;
}
