// RUN: objc3c -fobjc-version=3 -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -O2 -fsyntax-only %s
#ifndef __OBJC3_FEATURE_M26_C_010_CI_AUTOMATION_ENFORCEMENT_SHAPE
#define __OBJC3_FEATURE_M26_C_010_CI_AUTOMATION_ENFORCEMENT_SHAPE 1
#endif

typedef signed int m26c010_i32;
typedef _Bool m26c010_bool;

typedef m26c010_i32 (*M26C010_BuildStepFn)(m26c010_bool strict_mode, m26c010_i32 baseline);
typedef m26c010_bool (*M26C010_EnforcementGateFn)(m26c010_i32 step_code);

static m26c010_i32 m26c010_build_step(m26c010_bool strict_mode, m26c010_i32 baseline) {
    return strict_mode ? baseline + 2 : baseline + 1;
}

static m26c010_bool m26c010_enforcement_gate(m26c010_i32 step_code) {
    return step_code >= 4;
}

static m26c010_i32 m26c010_ci_shape_digest(M26C010_BuildStepFn build_step, M26C010_EnforcementGateFn gate) {
    m26c010_i32 step_a = build_step(0, 2);
    m26c010_i32 step_b = build_step(1, 2);
    m26c010_i32 step_c = build_step(1, 3);
    return (gate(step_a) ? 1 : 0) | ((gate(step_b) ? 1 : 0) << 1) | ((gate(step_c) ? 1 : 0) << 2);
}

typedef struct M26C010_Probe {
    int enabled;
    int sequence;
    int issue;
    m26c010_i32 ci_shape_digest;
    m26c010_bool final_gate_state;
} M26C010_Probe;

M26C010_Probe m26c010_probe(void) {
#if __OBJC3_FEATURE_M26_C_010_CI_AUTOMATION_ENFORCEMENT_SHAPE
    M26C010_BuildStepFn build_step = m26c010_build_step;
    M26C010_EnforcementGateFn gate = m26c010_enforcement_gate;
    m26c010_i32 digest = m26c010_ci_shape_digest(build_step, gate);
    M26C010_Probe probe = { 1, 10, 2865, digest, gate(digest) };
#else
    M26C010_Probe probe = { 0, 10, 2865, 0, 0 };
#endif
    return probe;
}
