// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict-system -O0 -fsyntax-only %s
// RUN: objc3c -fobjc-version=3 -fobjc-arc -fobjc3-strictness=strict-system -O2 -fsyntax-only %s
typedef int res_handle_t;
static int res04_trace = 0;
static int res04_close_count_a = 0;
static int res04_close_count_b = 0;

void res04_close_a(res_handle_t handle) {
    if (handle != -1) {
        res04_trace = res04_trace * 10 + 1;
        res04_close_count_a = res04_close_count_a + 1;
    }
}

void res04_close_b(res_handle_t handle) {
    if (handle != -1) {
        res04_trace = res04_trace * 10 + 2;
        res04_close_count_b = res04_close_count_b + 1;
    }
}

int res04_normal_scope_exit(void) {
    res04_trace = 0;
    res04_close_count_a = 0;
    res04_close_count_b = 0;

    {
        res_handle_t a __attribute__((objc_resource(close=res04_close_a, invalid=-1))) = 10;
        res_handle_t b __attribute__((objc_resource(close=res04_close_b, invalid=-1))) = 20;
        defer { res04_trace = res04_trace * 10 + 3; };
        (void)a;
        (void)b;
    }

    if (res04_close_count_a != 1 || res04_close_count_b != 1) {
        return -1;
    }

    return res04_trace;
}
