// RUN: objc3c -fobjc-version=3 --emit-module IFC55A.objcmod --emit-interface IFC55A.obji %s
// RUN: objc3c --verify-interface IFC55A.objcmod IFC55A.obji %s
#pragma objc assume_nonnull begin
__attribute__((objc_unsafe_sendable))
@interface IFC55CanonicalBox : NSObject
- (id _Nullable)cachedToken __attribute__((objc_nonisolated));
- (void)enqueue:(id _Nonnull)item __attribute__((objc_executor(main)));
@end
#pragma objc assume_nonnull end

