// RUN: objc3c -fobjc-version=3 -fobjc3-strictness=strict --emit-module INTSWIFT05.objcmod --emit-interface INTSWIFT05.obji --emit-interface-mode=text --emit-interface-format=deterministic %s
// RUN: objc3c --emit-module INTSWIFT05-import.objcmod --import-interface INTSWIFT05.obji --emit-interface INTSWIFT05-roundtrip.obji --emit-interface-mode=text --emit-interface-format=deterministic
// RUN: objc3c --verify-interface INTSWIFT05.objcmod INTSWIFT05-roundtrip.obji %s
// RUN: objc3c --verify-interface INTSWIFT05-import.objcmod INTSWIFT05.obji %s
#pragma objc assume_nonnull begin
@interface INTSWIFT05Surface : NSObject
- (id _Nullable)lookup:(id _Nullable)key;
- (id?)lookupAlias:(id?)key;
- (id!)legacyValue;
- (id _Nonnull)decode:(id _Nonnull)payload throws;
- (BOOL)decodeNSError:(id _Nonnull)payload
                value:(id _Nullable * _Nullable)outValue
                error:(id<Error> _Nullable * _Nullable)outError __attribute__((objc_nserror));
@end
#pragma objc assume_nonnull end
