// RUN: objc3c -fobjc-version=3 -fobjc3-feature-opt-meta=on --emit-module METAEXP01-A.objcmod --emit-interface METAEXP01-A.obji --emit-interface-mode=text --emit-interface-format=deterministic %s
// RUN: objc3c -fobjc-version=3 -fobjc3-feature-opt-meta=on --emit-module METAEXP01-B.objcmod --emit-interface METAEXP01-B.obji --emit-interface-mode=text --emit-interface-format=deterministic %s
// RUN: diff -u METAEXP01-A.obji METAEXP01-B.obji
// RUN: objc3c --verify-interface METAEXP01-A.objcmod METAEXP01-B.obji %s
#if !__OBJC3_FEATURE_DERIVE_EQUALITY__
#error "META-EXP-01 requires __OBJC3_FEATURE_DERIVE_EQUALITY__"
#endif
#if !__OBJC3_FEATURE_DERIVE_HASH__
#error "META-EXP-01 requires __OBJC3_FEATURE_DERIVE_HASH__"
#endif
#if !__OBJC3_FEATURE_DERIVE_DEBUGDESCRIPTION__
#error "META-EXP-01 requires __OBJC3_FEATURE_DERIVE_DEBUGDESCRIPTION__"
#endif
__attribute__((objc_derive("DebugDescription")))
__attribute__((objc_derive("Equality")))
__attribute__((objc_derive("Hash")))
@interface METAEXP01Record : NSObject
@property (nonatomic) int revision;
@property (nonatomic, copy) NSString * _Nonnull token;
@end
