// RUN: objc3c -fobjc-version=3 --emit-module SYSATTR05.objcmod --emit-interface SYSATTR05.obji %s
// RUN: objc3c --verify-interface SYSATTR05.objcmod SYSATTR05.obji %s
typedef int objc_handle_t;
void close_fd(objc_handle_t fd);
