id fetchValue(void) async;
id fetchFailing(void) throws async;
id fetchAlt(void) async throws;

id parse_async_grammar(void) async throws {
    id a = await fetchValue();
    id b = try await fetchFailing();
    id c = await try fetchFailing();
    id d = await (fetchAlt());
    return a ? b : (c ? c : d);
}
